import { createAsyncThunk } from '@reduxjs/toolkit';
import * as api from '../../api/index';

export const fetchPostsThunk = createAsyncThunk("posts/getPosts",
    async (dispatch) => {        
        const { data } = await api.fetchPosts();
    }
);

export const createPost = createAsyncThunk("posts/getPosts",
    async (newPost) => {
        const { data } = await api.createPost(newPost);
        return data;
    }
);
