import React, { useEffect, useState } from "react"
import { GiHamburgerMenu } from 'react-icons/gi';
import { CgClose } from 'react-icons/cg';

import logo from "../images/logo.png";
import { logout } from "../actions/actions";
import decode from 'jwt-decode';

import { Link} from "react-router-dom";
import { useDispatch } from "react-redux";
import { useLocation } from "react-router-dom";

const Header = () => {  
    
    const[user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const location = useLocation();

    const[mobileMenu, setMobileMenu] = useState(false);
    
    const handleMobileMenu = () => {
        setMobileMenu(!mobileMenu);
        if(mobileMenu) {
            document.getElementById('mobile-menu').classList.toggle('visible');
        } else {
            document.getElementById('mobile-menu').classList.toggle('hidden');
        }
    }

    const handleLogout = () => {
        dispatch(logout());
        setUser(null);
    }

    useEffect(() => {
        const token = user?.token;
    
        if (token) {
          const decodedToken = decode(token);
    
          if (decodedToken.exp * 1000 < new Date().getTime())
                handleLogout();
        }
    
        setUser(JSON.parse(localStorage.getItem('profile')));
      }, [location]);
    
    return(
        <nav className="w-full bg-white border-gray-300 px-2 sm:px-4 py-2.5 rounded mb-2 md:border-b">
            <div class="flex justify-between flex-wrap items-center">
                <div>
                    <Link to='/'>
                        <img src={logo} alt="logo" className="md:w-40 sm:w-20"/>
                    </Link>
                </div>
                <div className="hidden md:block w-full bg-gray-50 p-2">
                            <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                                <Link to='/'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-white bg-red-400 rounded md:bg-transparent md:text-red-500 md:p-0 dark:text-white" aria-current="page">Feed</a>
                                    </li>
                                </Link>
                                {
                                    user &&
                                    <Link to='/post'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">
                                         Post
                                        </a>
                                    </li>
                                    </Link>
                                }
                                <Link to='/auth'>
                                    <li>
                                        <a onClick={user && handleLogout } className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">{user?'Sign Out':'Sign In'}</a>
                                    </li>
                                </Link>
                            </ul>
                </div>
                                <div className="sm:hidden md:block w-full bg-gray-50 p-2" id="mobile-menu">
                            <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                                <Link to='/'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-white bg-red-400 rounded md:bg-transparent md:text-red-500 md:p-0 dark:text-white" aria-current="page">Feed</a>
                                    </li>
                                </Link>
                                {
                                    user &&
                                    <Link to='/post'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">
                                         Post
                                        </a>
                                    </li>
                                    </Link>
                                }
                                <Link to='/auth'>
                                    <li>
                                        <a onClick={user && handleLogout } className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">{user?'Sign Out':'Sign In'}</a>
                                    </li>
                                </Link>
                            </ul>
                </div>

            </div>
            {
            mobileMenu &&
            <div className="md:hidden w-full bg-gray-50 p-2" id="mobile-menu">
                <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                    <Link to='/'>
                        <li>
                            <a className="block py-2 pr-4 pl-3 text-white bg-red-400 rounded md:bg-transparent md:text-red-500 md:p-0 dark:text-white" aria-current="page">Feed</a>
                        </li>
                    </Link>
                    {
                        user &&
                        <Link to='/post'>
                        <li>
                            <a className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">
                             Post
                            </a>
                        </li>
                        </Link>
                    }
                    <Link to='/auth'>
                        <li>
                            <a onClick={user && handleLogout } className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">{user?'Sign Out':'Sign In'}</a>
                        </li>
                    </Link>
                </ul>
            </div>
            }
        </nav>
)}


export default Header;
