import React, { useState } from "react";
import { GoogleLogin } from '@react-oauth/google';
import { useDispatch } from 'react-redux';
import { authThunk } from "../../actions/PostActions";

const Auth = () => {

    const dispatch = useDispatch();
    const[isSignUp, setIsSignUp] = useState(true);


    const handleOnSubmit = (e) => {
        e.preventDefault();
    }

    const switchMode = (e)  => {
        e.preventDefault();
        setIsSignUp(!isSignUp);
    }

    const googleSuccess = async (res) => {
        const result = res?.profileObj;
        const token = res?.tokenId;

        try {
            dispatch(authThunk({result, token}))
        } catch(err) {

        }
    }
    const googleFailure = (err) => {
        console.log(err)    
        console.log('google authentication was unsuccessfull, try again')        
    }

    return(
        <div className="md:w-1/3 mt-5 w-full md:mx-auto">
            <form className="p-5 shadow-lg space-y-2" enctype="multipart/form-data" onSubmit={handleOnSubmit}>
                <h1 className="flex justify-center">{ isSignUp ?  'Sign Up' : 'Sign In' }</h1>
                    
                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300" name ="creator" type="email" placeholder="Email Address*" required/>
                </div>
                {
                    isSignUp && 
                    <div className="w-full flex space-x-2"> 
                        <div className="">
                            <input className="w-full rounded border border-gray-300" name="first-name" type="text"   placeholder="First Name*" required/>
                        </div>

                        <div className="">
                            <input className="w-full rounded border border-gray-300" name="last-name" type="text"   placeholder="Last Name*" required/>
                        </div>
                    </div>
                }

                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300" name="password" type={isSignUp ? 'text':'password'}   placeholder="Password*" required/>
                </div>
                {
                    isSignUp &&
                    <div className="space-y-1">     
                        <input className="w-full rounded border border-gray-300" name="confirm-password" type={isSignUp ? 'text':'password'}  placeholder="Confirm Password*" required/>
                    </div>
                }
                <div className="w-full mt-3">
                    <button type="submit" className="w-full p-3 bg-red-400 text-white rounded">{ isSignUp?'Sign Up':'Sign In'}</button>
                    <button onClick={switchMode} className="w-full p-3 bg-gray-50 rounded">{ isSignUp?'Already have an account? Sign In here':"don't have an account? Sign Up here"}</button>
                </div>   
                <div className="flex justify-center">
                </div>
            </form>
        </div>   
    )
}


export default Auth;
