import { ThemeProvider, Container, AppBar, Typography, Grow, Grid, createMuiTheme } from '@mui/material';
import { createTheme } from '@mui/system';

import Form from './components/Forms/Form';
import Posts from './components/Posts/Posts';

import logo from './images/logo.png';
import useStyles from './styles';

const theme = createTheme();

function App() {

  const classes = useStyles();
  return (
    <ThemeProvider theme={theme}>
      <Container maxWidth="xl">
          <AppBar className={classes.appBar} position="static" color="inherit">
              <Typography className={classes.heading} variant="h2" align="center">Instagram</Typography>
              <img className={classes.image} src={logo} alt='instagram' height={60} width={60} />
          </AppBar>
        <Grow in>
            <Container>
                <Grid container justifyItems="space-between" alignItems="stretch" spacing={3}>
                    <Grid item xs={12} sm={7}>
                        <Posts/>
                    </Grid>
                    <Grid item xs={12} sm={7}>
                        <Form/>
                    </Grid>
                </Grid>
            </Container>
        </Grow>
      </Container>
    </ThemeProvider>
  );
}

export default App;
