import { configureStore } from "@reduxjs/toolkit";
import  AuthReducer  from "../components/Auth/AuthSlice";
import PostReducer from "../components/Posts/PostSlice";

const store = configureStore({
    reducer: {
        posts: PostReducer,
        auth: AuthReducer
    }
});

export default store;
