import axios from 'axios';

const url = 'http://localhost:5000/posts';

export const fetchPosts = () => axios.get(`http://localhost:5000/posts/getPosts`);
export const createPost = (newPost) => axios.post(`http://localhost:5000/posts/createPost`, newPost);
