import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import Posts from "./Posts/Posts";

const PaginatedPosts = () => {
  
  const posts = useSelector((state) => state.posts.posts);
  const pageSize = 6;
  const[currentPage, setCurrentPage] = useState(1);
  const[currentPosts, setCurrentPosts] = useState();
  
  useEffect(() => {
    const indexOfLastPost = currentPage * pageSize;
    const indexOfFirstPost = indexOfLastPost - pageSize;
    setCurrentPosts(posts.slice(indexOfFirstPost, indexOfLastPost));  
  }, [currentPage])

  const handleClick = (e) => {
    setCurrentPage(Number(e.id));
  }
  const pageNumbers = [];
  for (let i = 1; i <= Math.ceil(posts.length / pageSize); i++) {
    pageNumbers.push(i);
  }

  return(
    <div className="">
        <Posts posts={currentPosts}/>
        <div>
          <ul className="flex space-x-2 justify-center items-center">
            <svg className="fill-red-600" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 16 16">
            <path d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0zm3.5 7.5a.5.5 0 0 1 0 1H5.707l2.147 2.146a.5.5 0 0 1-.708.708l-3-3a.5.5 0 0 1 0-.708l3-3a.5.5 0 1 1 .708.708L5.707 7.5H11.5z"/>
            </svg>
          {
            pageNumbers.map(number => {
              return <li
                key={number}
                id={number}
                onClick={handleClick}
                className="text-blue-600 font-semibold text-lg"
              >
                {number}
              </li>
            })
          }
          <svg className="fill-red-600" xmlns="http://www.w3.org/2000/svg" width="20" height="20" viewBox="0 0 16 16">
            <path d="M8 0a8 8 0 1 1 0 16A8 8 0 0 1 8 0zM4.5 7.5a.5.5 0 0 0 0 1h5.793l-2.147 2.146a.5.5 0 0 0 .708.708l3-3a.5.5 0 0 0 0-.708l-3-3a.5.5 0 1 0-.708.708L10.293 7.5H4.5z"/>
          </svg>
          </ul>
        </div>
  </div>
)
}

export default PaginatedPosts;
