import React, { useState } from "react"
import { GiHamburgerMenu } from 'react-icons/gi';
import { CgClose } from 'react-icons/cg';

import logo from "../images/logo.png";
import { Link, NavLink } from "react-router-dom";

const Header = () => {

    const[areaExpanded, setAriaExpanded] = useState(false);

    const handleArea = () => {
        setAriaExpanded(!areaExpanded);
    }
    
    return(
        <nav class="md:container bg-white border-gray-200 px-2 sm:px-4 py-2.5 rounded mb-2 md:border-b md:border-gray-300">
            <div class="flex justify-between flex-wrap items-center">
                <div>
                    <img src={logo} alt="logo" className="md:w-40 sm:w-40"/>
                </div>
                <div className="md:hidden">
                    <button onClick={handleArea}>
                        {
                            areaExpanded ? <CgClose/> : <GiHamburgerMenu/> 
                        }
                    </button>
                </div>
            {
                areaExpanded && 
                <div class="w-full md:block md:w-auto bg-gray-50">
                    <ul class="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                        <Link to='/'>
                            <li>
                                <a href="#" class="block py-2 pr-4 pl-3 text-white bg-pink-700 rounded md:bg-transparent md:text-blue-700 md:p-0 dark:text-white" aria-current="page">Feed</a>
                            </li>
                        </Link>
                        <Link to='/post'>
                            <li>
                                <a href="#" class="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">Post</a>
                            </li>
                        </Link>
                        <li>
                            <a href="#" class="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">Services</a>
                        </li>
                        <li>
                            <a href="#" class="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">Pricing</a>
                        </li>
                        <li>
                            <a href="#" class="block py-2 pr-4 pl-3 text-gray-700 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent">Contact</a>
                        </li>
                    </ul>
                </div>
            }
            </div>
        </nav>
)}


export default Header;
