import React from "react";
import Post from "./Post/Post";
import { useSelector } from "react-redux";


const Posts = ({setCurrentId, user}) => {

     const posts = useSelector((state) => state.posts.posts);
     
    return (
            <div className="w-full grid grid-flow-row md:mx-5">
                <div className="grid md:grid-cols-3 md:gap-10 sm:gap-2">
                        {
                            posts.map(
                                post => {
                                    return <Post post={post} setCurrentId={setCurrentId} user={user}/>
                                }
                            )
                        }
                </div>    
            </div>
//        </div>
    )
}

export default Posts;
