import axios from 'axios';

const API = axios.create({ baseURL: 'http://localhost:5000'})

API.interceptors.request.use((req => {
    if(localStorage.getItem('profile')) {
        req.headers.Authorization = `Bearer ${JSON.parse(localStorage.getItem('profile')).token}`;
        req.headers.ContentType = 'multipart/form-data'
    }

    return req;
}))

export const getPosts = () => API.get(`/posts/getPosts`);

export const createPost = (newPost) => API.post(`/posts/createPost`,newPost);


export const updatePost = (postId, post) => API.patch({
        method:'patch',
        url:`/posts/updatePost/${postId}`,
        data: post,
        headers: {
            'content-type': 'multipart/form-data'
        }        
});

export const deletePost = (postId, profile) => API.delete({
    method: 'delete',
    url:`/posts/deletePost/${postId}`,
    headers: {
        'authorization': profile
    }
});

export const likePost = (postId, profile) => API.patch({
    method: 'patch',
    url: `/posts//likePost/${postId}`,
    headers: {
        'authorization': profile
    }
    
});

export const signIn = (formData) => axios.post(`/user/signin`, formData);
export const signUp = (formData) => axios.post(`/user/signup`, formData);
