import React, { useState } from "react";

const Auth = () => {

    const[isSignUp, setIsSignUp] = useState(false);

    const handleOnSubmit = () => {

    }
    return(
        <div className="md:w-1/3 mt-5 w-full md:mx-auto">
            <form className="p-5 shadow-lg space-y-2" enctype="multipart/form-data" onSubmit={handleOnSubmit}>
                <h1 className="flex justify-center">{ isSignUp ?  'Sign Up' : 'Sign In' }</h1>
                    
                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300" name ="creator" type="email" placeholder="Email Address" required/>
                </div>
                {
                    isSignUp && 
                    <> 
                        <div className="space-y-1">
                            <input className="w-full rounded border border-gray-300" name="first-name" type="text"   placeholder="First Name" required/>
                        </div>

                        <div className="space-y-1">
                            <input className="w-full rounded border border-gray-300" name="last-name" type="text"   placeholder="Last Name" required/>
                        </div>
                    </>
                }
                <div className="flex w-full mt-3">
                    <button type="submit" className="w-full p-3 bg-red-400 text-white rounded">Sign In</button>
                </div>   
            </form>
        </div>   
    )
}


export default Auth;
