import { createSlice } from "@reduxjs/toolkit";


const PostSlice = createSlice({
    name:"posts",
    initialState: {
        posts: []
    },
    reducers: {
        getPostsReducer(state, action) {
            state.posts = action.payload;
        },
        createPostReducer(state, action) {
            state.posts.push(action.payload);          
        },
        updatePostReducer(state, action) {
            state.posts.map(post => post._id === action.payload._id ? action.payload : post)          
        }
    }
})

export const { getPostsReducer, createPostReducer, updatePostReducer } = PostSlice.actions;

export default PostSlice.reducer;
