import React, { useState } from "react"
import { useDispatch } from "react-redux";
import { createPostThunk } from "../../actions/PostActions";

const Form = () => {
    const dispatch = useDispatch();
    const [postData, setPostData] = useState({
        creator: "",
        title: "",
        tags: "", 
        message: "",
        selectedFile: ""
    })  

    const handleOnSubmit = (e) => {
        e.preventDefault();

        dispatch(createPostThunk(postData)); 
    }

    return (
        <div className="">
            <form className="w-full max-w-sm">
                <input name="creator" type="text" className="form-input border border-gray-300  rounded "/> 
                <input name="title" type="text" className="form-input border border-gray-300  rounded "/> 
                <input name="tags" type="text" className="form-input border border-gray-300  rounded "/> 
                <input name="message" type="text" className="form-input border border-gray-300  rounded "/> 
            </form>
        </div>
    )
}

export default Form;
