import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import Posts from './components/Posts/Posts';
import { fetchPostsThunk } from './actions/PostActions';
import Header from './components/header';
import Footer from './components/footer';
import { BrowserRouter as Router,  Route, Routes } from 'react-router-dom';
import Form from './components/Forms/Form';
import Auth from './components/Auth/Auth';
import { GoogleOAuthProvider } from '@react-oauth/google';

const App = () => {

  const[currentId, setCurrentId] = useState();

  const dispatch = useDispatch();

  useEffect(() => {
     dispatch(fetchPostsThunk());
  },[currentId, dispatch])

  return (
    <GoogleOAuthProvider clientId='817141185581-mk5c1e1opa0a17qepj5qs7vrjbbbf7ue.apps.googleusercontent.com'>

    <div className="w-full md:mx-auto md:w-2/3">
      <Router> 
        <Header/>
        <Routes>
          <Route path='/' element={<Posts setCurrentId={setCurrentId} />} />
          <Route path='/post' element={<Form currentId={currentId} setCurrentId={setCurrentId}/>}/>
          <Route path='/auth' element={<Auth/>}/>
        </Routes>
        <Footer/>
      </Router>  
    </div>
    </GoogleOAuthProvider>
  );
}

export default App;
