import { createAsyncThunk } from "@reduxjs/toolkit"
import { fetchPosts } from "../../api"

export const fetchPostsThunk = createAsyncThunk(
    "FETCH_ALL", async () => {
        const  { data } =  await fetchPosts();
        return data;
    } 
)
