export const PostReducer = (state = { posts: [] }, action) => {
    
    switch(action.type) {
        case 'FETCH_ALL': {
            state.posts = action.payload;
        }
        case 'CREATE': {
            state.posts.push(action.payload);
        }
    }

    return state.posts;
}
