import React, { useState } from "react"
import { useDispatch } from "react-redux";
import { createPostThunk } from "../../actions/PostActions";

const Form = () => {
    const dispatch = useDispatch();
    const [postData, setPostData] = useState({
        creator: "",
        title: "",
        tags: "", 
        message: "",
        selectedFile: ""
    })  

    const handleOnSubmit = (e) => {
        e.preventDefault();

        dispatch(createPostThunk(postData)); 
    }

    return (
        <div className="md:container md:w-3/6 bg-red-100 w-full md:mx-auto h-screen">
            <div className="flex">
                <label htmlFor="creator">Creator</label>
                <input name="creator" type="text" value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value})}/>
            </div>
        </div>
    )
}

export default Form;
