import React, { useState } from "react"
import { useDispatch } from "react-redux";
import { createPostThunk } from "../../actions/PostActions";
import FileBase64 from 'react-file-base64';

const Form = () => {
    const dispatch = useDispatch();
    const [postData, setPostData] = useState({
        creator: "",
        title: "",
        tags: "", 
        message: "",
        selectedFile: ""
    })  

    const handleOnSubmit = (e) => {
        e.preventDefault();

        dispatch(createPostThunk(postData)); 
    }

    return (
        <div className="md:container md:w-1/3 mt-5 w-full md:mx-auto h-screen">
            <form className="p-5 shadow-lg space-y-2" onSubmit={handleOnSubmit}>
                <div className="space-y-1">
                    <label className="w-auto block" htmlFor="creator">Creator</label>
                    <input className="w-full rounded border border-gray-300" name ="creator" type="text" value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value})}/>
                </div>
                <div className="flex">
                    <label htmlFor="title">Title</label>
                    <input name="title" type="text" value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value})}/>
                </div>
                <div className="flex">
                    <label htmlFor="message">Message</label>
                    <input name="message" type="text" value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value})}/>
                </div>
                <div className="flex">
                    <label htmlFor="tags">Tags</label>
                    <input name="tags" type="text" value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value})}/>
                </div>
                <div className="flex">
                    <label htmlFor="select-file">Select File</label>
                    <FileBase64
                        type="file"
                        multiple={false}
                        onDone={(base64) => setPostData({ ...postData, selectedFile: base64})}
                    />
                </div>
                <div className="flex w-full">
                    <button type="submit" className="w-1/2 p-3 bg-red-300 text-white rounded-l">Submit</button>
                    <button type="reset" className="w-1/2 p-3 bg-gray-200 rounded-r">Clear</button>
                </div>
            </form>
        </div>
    )
}

export default Form;
