import { createPost, getPosts, updatePost } from "../api"
import { getPostsReducer, updatePostReducer, createPostReducer } from "../reducers/PostSlice";

export const fetchPostsThunk = () => async (dispatch) => {
    const { data } = await getPosts();
    dispatch(getPostsReducer(data));
}
  
export const createPostThunk = (newPost) => async (dispatch) => {
    const { data } = await createPost(newPost);
    dispatch(createPostReducer(data));
}

export const updatePostThunk = (currentId, post) => async (dispatch) => {
    const { data } = await updatePost(currentId, post);
    dispatch(updatePostReducer(data));
}
