import axios from 'axios';

const url = 'http://localhost:5000/';

export const getPosts = () => axios.get(`${url}posts/getPosts`);

export const createPost = (newPost, profile) => axios({
        method: 'post',
        url: `${url}posts/createPost`,
        data: newPost,
        headers: {
            'content-type': 'multipart/form-data',
            'authorization': profile
        }        
    });


export const updatePost = (postId, post, profile) => axios({
        method:'patch',
        url:`${url}posts/updatePost/${postId}`,
        data: post,
        headers: {
            'content-type': 'multipart/form-data',
            'authorization': profile
        }        
});

export const deletePost = (postId, profile) => axios({
    method: 'delete',
    url:`${url}posts/deletePost/${postId}`,
    headers: {
        'authorization': profile
    }
});

export const likePost = (postId, profile) => axios({
    method: 'patch',
    url: `${url}posts//likePost/${postId}`,
    headers: {
        'authorization': profile
    }
    
});

export const signIn = (formData) => axios.post(`${url}user/signin`, formData);
export const signUp = (formData) => axios.post(`${url}user/signup`, formData);
