import React, { useState } from "react";
import moment from 'moment';
import { VscHeart } from 'react-icons/vsc'
import { FcLike} from 'react-icons/fc'
import { BsFillSuitHeartFill } from 'react-icons/bs'
import { AiOutlineDelete } from 'react-icons/ai';
import { HiDotsHorizontal } from 'react-icons/hi';
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { deletePostThunk } from "../../../actions/PostActions";

const Post = ({post, setCurrentId}) => {

    const[drawer, setDrawer] = useState(false);
    const navigateTo = useNavigate();
    const dispatch = useDispatch();

    const handleUpdate = () => {
      setCurrentId(post._id);
      navigateTo('/post');
    }

    const handleDelete = () => {
         dispatch(deletePostThunk(post._id));    
    }

    return (
        <div className="w-full">
          <div className="sm:border-t-2 md:border-0 sm:border-gray-300 bg-white rounded-xl shadow-lg transform transition duration-500  hover:shadow-2xl">
            <div className="flex justify-between">
                <div>
                  <div className="flex items-center space-x-2 mt-1">
                      <img className="w-10 rounded-full" src="https://d2qp0siotla746.cloudfront.net/img/use-cases/profile-picture/template_3.jpg" alt="user" />
                      <h2 className="text-gray-800 font-bold cursor-pointer">Felipe Sacudon</h2>
                  </div>
                  <h2 className="ml-1 mt-1 mb-2 text-gray-700 hover:underline cursor-pointer">{moment(post.createdAt).fromNow()}</h2>
                </div>
                <div className="m-2">
                  <button onClick={() => setDrawer(!drawer)}>
                      <HiDotsHorizontal/>                    
                  </button>
                  {
                    drawer && <div>
                      <button onClick={handleUpdate}>
                          Edit
                      </button>                
                      <button onClick={handleDelete}>
                          Delete
                      </button>
                    </div>
                  }
                </div>
            </div>
            <div>
               <img className="w-full h-min cursor-pointer" src={post.selectedFile} alt="post-image"/>
               <h1 className="ml-1">{post.title.substring(0,30)}</h1>
            </div>
            <div className="flex p-4 justify-between">
                <div className="flex space-x-1 items-center">
                  <span className="">
                    { 
                      post.likeCount == 0 ? <VscHeart className="w-5 h-5"/>: <FcLike className="w-5 h-5"/>
                    }
                  </span>
                  <span>{post.likeCount}</span>
                </div>
                <div>
                </div>
            </div>
          </div>
        </div>
    )
}

export default Post;
