import React, { useState } from "react";
import moment from 'moment';
import { HiDotsHorizontal } from 'react-icons/hi';
import { useNavigate } from "react-router-dom";
import { useDispatch } from "react-redux";
import { deletePost, likePost } from "../../../actions/actions";

const Post = ({post, setCurrentId}) => {

    const[user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));

    const[drawer, setDrawer] = useState(false);
    const navigateTo = useNavigate();
    const dispatch = useDispatch();

    const handleUpdate = () => {
        setCurrentId(post._id);
        navigateTo('/post');
    }

    const handleDelete = () => {
         dispatch(deletePost(post._id));    
    }

    const handleLikes = () => {
      dispatch(likePost(post._id));
    }

    return (
        <div className="w-full">
          <div className="sm:border-t-2 md:border-0 sm:border-gray-300 bg-white rounded-xl shadow-lg transform transition duration-500  hover:shadow-2xl">
            <div className="flex justify-between">
                <div>
                  <div className="flex items-center space-x-2 mt-1">
                      {/* <img className="w-10 h-10 rounded-full" src={ProfileImage} alt="user-profile-image" /> */}
                      <h2 className="text-gray-800 font-bold cursor-pointer">{post.name}</h2>
                  </div>
                  <h2 className="ml-1 mt-1 mb-2 text-gray-700 hover:underline cursor-pointer">{moment(post.createdAt).fromNow()}</h2>
                </div>
                <div className="m-2">
                  {
                    user?.result?._id === post?.creator && 
                    <button onClick={() => setDrawer(!drawer)}>
                        <HiDotsHorizontal/>                    
                    </button>
                  }
                  {
                      drawer && <div className="absolute right-0 bg-white flex flex-col items-start">
                        <button className="p-2" onClick={handleUpdate}>
                            Edit
                        </button>                
                        <button className="p-2" onClick={handleDelete}>
                            Delete
                        </button>
                      </div>
                  }
                </div>
            </div>
            <div>
                <div>
                    <img className='w-full h-min cursor-pointer' src={post.selectedFile} alt="post-image"/>
                </div>
                <div className="m-1">
                    <p>
                        {
                        post.message    
                        }
                    </p>
                </div>
            </div>
            <div className="flex p-4 justify-between">
                <div className="cursor-pointer flex items-center space-x-3">
                  
                  <span onClick={ handleLikes}>Like</span>
                  {
                    post.likes.length > 0 ?
                    <div>
                    </div>
                    :
                    <div>
                    </div>
                  }
                </div>
                <div>
                </div>
            </div>
          </div>
        </div>
    )
}

export default Post;
