import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Redirect } from 'react-router-dom';
import { fetchPosts } from './actions/actions';
import Header from './components/header';
import Footer from './components/footer';
import { BrowserRouter as Router,  Route, Routes } from 'react-router-dom';
import UploadPost from './components/Forms/UploadPost';
import Auth from './components/Auth/Auth';
import PaginatedItems from './components/pagination';

const App = () => {
  const[currentId, setCurrentId] = useState();
  const dispatch = useDispatch();
  const posts = useSelector(state => state.posts.posts);
  const[user, setUser] = JSON.parse(localStorage.getItem('profile'));

  useEffect(() => {
     dispatch(fetchPosts());
     setUser(JSON.parse(localStorage.getItem('profile')));
  },[currentId, dispatch])

  return (
    <div className="w-full">
      <Router> 
        <Header/>
        <Routes>
          <Route path='/' exact element={<Auth  /> }>
            <Route path=':feed' exact element={<PaginatedItems setCurrentId={setCurrentId} items={posts} />} />
            <Route path=':post' exact element={<UploadPost currentId={currentId} setCurrentId={setCurrentId}/>}/>
          </Route>
        </Routes>
        <Footer/>
      </Router>  
    </div>
  );
}

export default App;
