import { Paper, TextField, Typography, Button, ButtonGroup } from "@mui/material";
import { Box } from "@mui/system";
import React, { useState } from "react"
import FileBase from "react-file-base64";
import { useDispatch } from "react-redux";
import { createPostThunk } from "../../actions/PostActions";
import Styles from "./styles";

const Form = () => {
    const dispatch = useDispatch();
    const [postData, setPostData] = useState({
        creator: "",
        title: "",
        tags: "", 
        message: "",
        selectedFile: ""
    })  

    const handleOnSubmit = (e) => {
        e.preventDefault();

        dispatch(createPostThunk(postData)); 
    }

    return (
        <Paper> 
                <form autoComplete="off" noValidate onSubmit={handleOnSubmit}>
                    <Typography variant="h6">Upload Post</Typography>
                    <TextField name="creator" variant="outlined" label="Creator" value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value})} fullWidth></TextField>
                    <TextField name="title" variant="outlined" label="Title" value={postData.title} onChange={(e) => setPostData({ ...postData, title: e.target.value})} fullWidth></TextField>
                    <TextField name="message" variant="outlined" label="Message" value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value})} fullWidth></TextField>
                    <TextField name="tags" variant="outlined" label="Tags" value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value})} fullWidth></TextField>
                    <div>
                        <FileBase 
                            type="file"
                            multiple={false}
                            onDone={(base64) => setPostData({ ...postData, selectedFile: base64})}
                        />
                    </div>
                    <ButtonGroup>
                            <Button variant="contained" color="primary" type="submit" size="large" fullWidth>
                                    <Box sx={Style.buttonSubmit}>Submit</Box>
                            </Button>
                            <Button variant="contained" color="secondary" type="reset" size="small" fullWidth>Clear</Button>
                    </ButtonGroup>
                </form>
        </Paper>
    )
}

export default Form;
