import { Container, AppBar, Typography, Grow, Grid, ThemeProvider, createTheme, Slider } from '@mui/material';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import {Box} from '@mui/material';

import Form from './components/Forms/Form';
import { fetchPostsThunk } from './actions/PostActions';
import Posts from './components/Posts/Posts';

import logo from './images/logo.png';
import Style from './styles';

const theme = createTheme();

const App = () => {

  const dispatch = useDispatch();

  useEffect(() => {
     dispatch(fetchPostsThunk());
  },[dispatch])

  return (
    <ThemeProvider theme={theme}>
          <Container maxWidth="lg">
              <AppBar position='static' color='inherit'>
                  <Box 
                      sx={Style.appBar}
                  >
                          <Typography variant="h6" align="left" fontFamily="billabong">Instagram</Typography>
                          <img src={logo}  alt="logo" height={60} width={60} />    
                  </Box>   
              </AppBar>
            <Grow in>
                <Container>
                    <Grid container justifyItems="space-between" alignItems="stretch" spacing={3}>
                        <Grid item xs={12} sm={7}>
                            <Posts/>                            
                        </Grid>
                        <Grid item xs={12} sm={7}>
                            <Form/>
                        </Grid>
                    </Grid>
                </Container>
            </Grow>
          </Container>
      </ThemeProvider>
  );
}

export default App;
