import { createPost, deletePost, getPosts, likePost, updatePost } from "../api"
import { getPostsReducer, updatePostReducer, createPostReducer, deletePostReducer } from "../reducers/PostSlice";

export const fetchPostsThunk = () => async (dispatch) => {
    const { data } = await getPosts();
    dispatch(getPostsReducer(data));
}
  
export const createPostThunk = (newPost) => async (dispatch) => {
    const {data}  = await createPost(newPost);
    dispatch(createPostReducer(data));
}

export const updatePostThunk = (currentId, post) => async (dispatch) => {
    await updatePost(currentId, post);
    dispatch(updatePostReducer(currentId));
}

export const deletePostThunk = (postId) => async (dispatch) => {
    await deletePost(postId);
    
    dispatch(deletePostReducer({id:postId}));
}

export const likePostThunk = (postId) => async (dispatch) => {
    await likePost(postId);
    dispatch(updatePostReducer(postId));
}

export const authThunk = (data) => async (dispatch) => {
}
