import axios from 'axios';

const url = 'http://localhost:5000/posts';

export const getPosts = () => axios.get(`${url}/getPosts`);

const header1= {
    'content-type': 'multipart/form-data'
}

const header2 = {
    'content-type': 'application/json'
}

export const createPost = (newPost) => { 

    let headers;

    if(newPost.selectedFile) 
        headers = header1;        
    else
        headers = header2;

    
    axios({
        method: 'post',
        url: `${url}/createPost`,
        data: newPost,
        headers: headers
    });
};

export const updatePost = (postId, post) => axios({
        method:'patch',
        url:`${url}/updatePost/${postId}`,
        data: post,
        headers: {
            'content-type': 'multipart/form-data'
        }        
});
