import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import Posts from './components/Posts/Posts';
import { fetchPosts } from './actions/actions';
import Header from './components/header';
import Footer from './components/footer';
import { BrowserRouter as Router,  Route, Routes } from 'react-router-dom';
import Form from './components/Forms/Form';
import Auth from './components/Auth/Auth';

const App = () => {

  const[user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));

  const[currentId, setCurrentId] = useState();

  const dispatch = useDispatch();

  useEffect(() => {
     dispatch(fetchPosts());
  },[currentId, user,  dispatch])

  return (
    <div className="w-full md:mx-auto md:w-2/3">
      <Router> 
        <Header user={user} setUser={setUser}/>
        <Routes>
          <Route path='/' element={<Posts setCurrentId={setCurrentId} user={user} />} />
          <Route path='/post' element={<Form currentId={currentId} setCurrentId={setCurrentId} profile={user}/>}/>
          <Route path='/auth' element={<Auth/>}/>
        </Routes>
        <Footer/>
      </Router>  
    </div>
  );
}

export default App;
