import React from "react";
import moment from 'moment';
import { VscHeart } from 'react-icons/vsc'
import { FcLike} from 'react-icons/fc'
import { BsFillSuitHeartFill } from 'react-icons/bs'
import { AiOutlineDelete } from 'react-icons/ai';
const Post = ({post}) => {
  console.log(post.selectedFile)
    return (
        <div className="w-full">
          <div className="sm:border-t-2 md:border-0 sm:border-gray-300 bg-white rounded-xl shadow-lg transform transition duration-500 hover:scale-105 hover:shadow-2xl">
            <div className="flex justify-between">
                      {/* <span className="text-white text-xs font-bold rounded-lg bg-green-500 inline-block mt-4 ml-4 mr-2 py-1.5 px-4 cursor-pointer ">
                          {post.tags.map((tag) => `#${tag} `)}
                      </span>    */}
                <div className="flex items-center space-x-2">
                    <img className="w-10 rounded-full" src="https://d2qp0siotla746.cloudfront.net/img/use-cases/profile-picture/template_3.jpg" alt="sara" />
                    <h2 className="text-gray-800 font-bold cursor-pointer">Felipe Sacudon</h2>
                </div>
                <p className="ml-4 mt-1 mb-2 text-gray-700 hover:underline cursor-pointer">{moment(post.createdAt).fromNow()}</p>
            </div>
            <img className="w-full h-auto cursor-pointer" src={post.selectedFile} alt="post-image"/>
            <div className="flex p-4 justify-between">
                <div className="flex space-x-1 items-center">
                  <span className="">
                    { 
                      post.likeCount == 0 ? <VscHeart className="w-5 h-5"/>: <FcLike className="w-5 h-5"/>
                    }
                  </span>
                  <span>{post.likeCount}</span>
                </div>
                <div>
                    <span><AiOutlineDelete/></span>
                </div>
            </div>
          </div>
        </div>
    )
}

export default Post;
