import { createSlice } from '@reduxjs/toolkit'


export const postsSlice = createSlice({
  name: 'posts',
  initialState: {
    posts: [],
  },
  reducers: {
     getPosts: (state, action) => {
        switch(action.type) {
            case 'FETCH_ALL':
                initialState.posts = action.payload;
            case 'CREATE':
                return state;
            default:
                return state;    
            }
    }
  }
})


export default postsSlice.reducer
