import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { useNavigate} from "react-router-dom";
import {  signIn, signUp } from "../../actions/actions";

const Auth = () => {

    const[formData, setFormData] = useState({
        firstName: '',
        lastName: '',
        email: '',
        password: '',
        confirmPassword: '',
    })

    const dispatch = useDispatch();
    const navigateTo = useNavigate();

    const[isSignUp, setIsSignUp] = useState(false);

    const handleOnSubmit = (e) => {
        e.preventDefault();

        if(isSignUp) 
            dispatch(signUp(formData, navigateTo));
        else
            dispatch(signIn(formData, navigateTo));
    }

    const switchMode = (e)  => {
        e.preventDefault();
        setIsSignUp(!isSignUp);
    }
    return(
        <div className="md:w-1/3 mt-5 w-full md:mx-auto">
            <form className="p-5 shadow-lg space-y-2" enctype="multipart/form-data" onSubmit={handleOnSubmit}>
                <h1 className="flex justify-center">{ isSignUp ?  'Sign Up' : 'Sign In' }</h1>
                    
                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300 p-2 text-gray-800" name ="creator" type="email" placeholder="Email Address*" value={formData.email} onChange={(e) => setFormData({...formData, email: e.target.value})} required/>
                </div>
                {
                    isSignUp && 
                    <div className="w-full flex space-x-2"> 
                        <div className="">
                            <input className="w-full rounded border border-gray-300 p-2" name="first-name" type="text"   placeholder="First Name*" value={formData.firstName} onChange={(e) => setFormData({...formData, firstName: e.target.value})} required/>
                        </div>

                        <div className="">
                            <input className="w-full rounded border border-gray-300 p-2" name="last-name" type="text"   placeholder="Last Name*" value={formData.lastName} onChange={(e) => setFormData({...formData, lastName: e.target.value})} required/>
                        </div>
                    </div>
                }

                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300 p-2" name="password" type='password'   placeholder="Password*" value={formData.password} onChange={(e) => setFormData({...formData, password: e.target.value})} required/>
                </div>
                {
                    isSignUp &&
                    <div className="space-y-1">     
                        <input className="w-full rounded border border-gray-300 p-2" name="confirm-password" type='password'  placeholder="Confirm Password*" value={formData.confirmPassword} onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})} required/>
                    </div>
                }
                <div className="w-full mt-3">
                    <button type="submit" className="w-full p-3 bg-red-400 text-white rounded">{ isSignUp?'Sign Up':'Sign In'}</button>
                    <button onClick={switchMode} className="w-full p-3 bg-gray-50 rounded">{ isSignUp?'Already have an account? Sign In here':"don't have an account? Sign Up here"}</button>
                </div>   
                <div className="flex justify-center">
                </div>
            </form>
        </div>   
    )   
}


export default Auth;
