import axios from 'axios';

const url = 'http://localhost:5000/posts';

export const getPosts = () => axios.get(`${url}/getPosts`);

export const createPost = (newPost) => axios({
        method: 'post',
        url: `${url}/createPost`,
        data: newPost,
        headers: {
            'content-type': 'multipart/form-data'
        }        
    });


export const updatePost = (postId, post) => axios({
        method:'patch',
        url:`${url}/updatePost/${postId}`,
        data: post,
        headers: {
            'content-type': 'multipart/form-data'
        }        
});

export const deletePost = (postId) => axios.delete(`${url}/deletePost/${postId}`)
