import React, { useEffect, useState } from "react"

import logo from "../images/logo.png";
import { logout } from "../actions/actions";
import decode from 'jwt-decode';

import { Link} from "react-router-dom";
import { useDispatch } from "react-redux";
import { useLocation } from "react-router-dom";

const Header = () => {  
    
    const[user, setUser] = useState(JSON.parse(localStorage.getItem('profile')));
    const dispatch = useDispatch();
    const location = useLocation();

    const[mobileMenu, setMobileMenu] = useState(false);
    
    const handleMobileMenu = () => {
        setMobileMenu(!mobileMenu);
    }

    const handleLogout = () => {
        dispatch(logout());
        setUser(null);
    }

    useEffect(() => {
        const token = user?.token;
    
        if (token) {
          const decodedToken = decode(token);
    
          if (decodedToken.exp * 1000 < new Date().getTime())
                handleLogout();
        }
    
        setUser(JSON.parse(localStorage.getItem('profile')));
      }, [location]);
    
    return(
        <nav className="w-full bg-white border-gray-300 px-2 sm:px-4 py-2.5 rounded mb-2 md:border-b">
            <div class="flex justify-between flex-wrap items-center">
                <div>
                    <Link to='/'>
                        <img src={logo} alt="logo" className="md:w-40 sm:w-20"/>
                    </Link>
                </div>
                <div className="flex bg-red-100">
                    <input type="search"  className="p-2 border border-gray-300 rounded-l"/>
                    <svg className="w-5 bg-gray-100 rounded-r border border-gray-300" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16">
                        <path d="M11.742 10.344a6.5 6.5 0 1 0-1.397 1.398h-.001c.03.04.062.078.098.115l3.85 3.85a1 1 0 0 0 1.415-1.414l-3.85-3.85a1.007 1.007 0 0 0-.115-.1zM12 6.5a5.5 5.5 0 1 1-11 0 5.5 5.5 0 0 1 11 0z"/>
                    </svg>                
                </div>
                <div className="md:hidden sm:block">
                    <button onClick={handleMobileMenu} className="">
                        {
                        mobileMenu ? 
                            <svg className="fill-gray-800" xmlns="http://www.w3.org/2000/svg" width="25" height="25" viewBox="0 0 16 16">
                                <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708z"/>
                            </svg>
                            :
                            <svg className="fill-gray-800" xmlns="http://www.w3.org/2000/svg" width="25" height="25" viewBox="0 0 16 16">
                                <path fill-rule="evenodd" d="M2.5 12a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5zm0-4a.5.5 0 0 1 .5-.5h10a.5.5 0 0 1 0 1H3a.5.5 0 0 1-.5-.5z"/>
                            </svg>
                        }
                    </button>
                </div>
                <div className="hidden md:block p-2">
                            <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                                <Link to='/'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-white bg-red-400 rounded md:bg-transparent md:text-red-500 md:p-0 dark:text-white" aria-current="page">Feed</a>
                                    </li>
                                </Link>
                                {
                                    user &&
                                    <Link to='/post'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">
                                         Post
                                        </a>
                                    </li>
                                    </Link>
                                }
                                <Link to='/auth'>
                                    <li>
                                        <a 
                                        onClick={user && handleLogout } 
                                        className="
                                        block 
                                        py-2 
                                        pr-4 
                                        pl-3 
                                        text-gray-700 
                                        border-b 
                                        border-gray-100 
                                        hover:bg-gray-50 
                                        md:hover:bg-transparent 
                                        md:border-0 
                                        md:p-0">{
                                        user?
                                        'Sign Out':
                                        'Sign In'
                                        }</a>
                                    </li>
                                </Link>
                            </ul>
                </div>
            </div>
            {
            mobileMenu &&
            <div className="md:hidden w-full p-2" id="mobile-menu">
                <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                    <Link to='/'>
                        <li>
                            <a className="block py-2 pr-4 pl-3 text-white bg-red-400 rounded">Feed</a>
                        </li>
                    </Link>
                    {
                        user &&
                        <Link to='/post'>
                            <li>
                                <a className="block py-2 pr-4 pl-3 text-gray-700 md:p-0 rounded">Post</a>
                            </li>
                        </Link>
                    }
                    <Link to='/auth'>
                        <li>
                            <a 
                            onClick={user && handleLogout }
                            className="block py-2 pr-4 pl-3 text-gray-700  md:p-0 rounded"
                            >{
                                user?
                            'Sign Out'
                            :
                            'Sign In'
                            }</a>
                        </li>
                    </Link>
                </ul>
            </div>
            }
        </nav>
)}


export default Header;
