import React, { useState } from "react"
import { GiHamburgerMenu } from 'react-icons/gi';
import { CgClose } from 'react-icons/cg';

import logo from "../images/logo.png";

import { Link} from "react-router-dom";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";

import { logoutReducer } from "./Auth/AuthSlice";

const Header = ({user, setUser}) => {

    const dispatch = useDispatch();
    const navigateTo = useNavigate();

    const[areaExpanded, setAriaExpanded] = useState(false);

    const handleArea = () => {
        setAriaExpanded(!areaExpanded);
    }

    const logout = () => {
        localStorage.clear();
        setUser(null);
        navigateTo('/auth');
    }
    
    return(
        <nav className="w-full bg-white border-gray-300 px-2 sm:px-4 py-2.5 rounded mb-2 md:border-b">
            <div class="flex justify-between flex-wrap items-center">
                <div>
                    <Link to='/'>
                        <img src={logo} alt="logo" className="md:w-40 sm:w-20"/>
                    </Link>
                </div>
                <div className="sm:hidden w-full md:block md:w-auto bg-gray-50">
                            <ul className="flex flex-col mt-4 md:flex-row md:space-x-8 md:mt-0 md:text-sm md:font-medium items-center">
                                <Link to='/'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-white bg-red-400 rounded md:bg-transparent md:text-red-500 md:p-0 dark:text-white" aria-current="page">Feed</a>
                                    </li>
                                </Link>
                                <Link to='/post'>
                                    <li>
                                        <a className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">Post</a>
                                    </li>
                                </Link>
                                <Link to='/auth'>
                                    <li>
                                        <a onClick={logout} className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 md:hover:bg-transparent md:border-0 md:hover:text-blue-700 md:p-0 dark:text-gray-400 md:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white md:dark:hover:bg-transparent dark:border-gray-700">{user?'Sign Out':'Sign In'}</a>
                                    </li>
                                </Link>
                            </ul>
                </div>

                <div className="md:hidden">
                        <button onClick={handleArea}>
                            {
                                areaExpanded ? <CgClose/> : <GiHamburgerMenu/> 
                            }
                        </button>
                    </div>
            </div>
        </nav>
)}


export default Header;
