import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchPosts } from './actions/actions';
import Header from './components/header';
import Footer from './components/footer';
import { BrowserRouter as Router,  Route, Routes } from 'react-router-dom';
import Form from './components/Forms/Form';
import Auth from './components/Auth/Auth';
import PaginatedPosts from './components/pagination';
import Posts from './components/Posts/Posts';
import Search from './components/search/Search';

const App = () => {
  const[currentId, setCurrentId] = useState();
  const dispatch = useDispatch();

  useEffect(() => {
     dispatch(fetchPosts());
  },[currentId, dispatch])

  return (
    <div className="w-full md:mx-auto md:w-2/3 bg-gray-100">
      <Router> 
        <Header/>
        <Routes>
          <Route path='/' exact element={<Posts setCurrentId={setCurrentId} />} />
          <Route path='/post' exact element={<Form currentId={currentId} setCurrentId={setCurrentId}/>}/>
          <Route path='/auth' exact element={<Auth/>}/>
        </Routes>
        <Footer/>
      </Router>  
    </div>
  );
}

export default App;
