import { Container, AppBar, Typography, Grow, Grid, ThemeProvider, createTheme } from '@mui/material';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';

import Form from './components/Forms/Form';
import Posts from './components/Posts/Posts';
import appStyles from './styles';

const theme = createTheme(appStyles);

const App = () => {

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch();
  },[])

  return (
      <ThemeProvider theme={theme}>
          <Container maxWidth="lg">
              <AppBar className={theme.appBar} position="static" color="inherit">
                  <Typography  className={theme.heading} variant="h6" align="left" fontFamily="billabong">Instagram</Typography>
              </AppBar>
            <Grow in>
                <Container>
                    <Grid container justifyItems="space-between" alignItems="stretch" spacing={3}>
                        <Grid item xs={12} sm={7}>
                            <Posts/>
                        </Grid>
                        <Grid item xs={12} sm={7}>
                            <Form/>
                        </Grid>
                    </Grid>
                </Container>
            </Grow>
          </Container>
      </ThemeProvider>
  );
}

export default App;
