import React, { useEffect, useState } from "react"
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { createPost, updatePost } from "../../actions/actions";
import { useSelector } from "react-redux";

const Form = ({ currentId, setCurrentId, profile}) => {

    const navigateTo = useNavigate();
    const dispatch = useDispatch();
    const post = useSelector((state) => currentId ? state.posts.posts.find((p) => p._id === currentId) : null);    
    
    const [postData, setPostData] = useState({
        tags: "", 
        message: "",
        selectedFile: ""
    })  

    useEffect(() => {
        if(post) {
            setPostData(post)
        }
    },[post])

    const handleOnSubmit =  async (e) => {
        e.preventDefault();
        if(currentId) {
           await dispatch(updatePost(currentId, postData));
        } 
        else {
           await dispatch(createPost(postData, profile)); 
        }

        clear();
        navigateTo('/');
    }

    const clear = () => {
        setCurrentId(null);
        setPostData({ selectedFile:'', message:'', tags:'' })
    }
    return (
        <div className="md:w-1/3 mt-5 w-full md:mx-auto">
            <form className="p-5 shadow-lg space-y-2" enctype="multipart/form-data" onSubmit={handleOnSubmit}>
                <div className="flex justify-center">
                    <h1 className="text-gra-700 font-medium">{post ? 'Edit': 'Upload'} a Post</h1>
                </div>
                {/* <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300" name ="creator" type="text" value={postData.creator} onChange={(e) => setPostData({ ...postData, creator: e.target.value})} placeholder="Creator"/>
                </div> */}
                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300 p-2  text-gray-800" name="message" type="text" value={postData.message} onChange={(e) => setPostData({ ...postData, message: e.target.value})} placeholder="Message" required/>
                </div>
                <div className="space-y-1">
                    <input className="w-full rounded border border-gray-300 p-2 text-gray-800" name="tags" type="text" value={postData.tags} onChange={(e) => setPostData({ ...postData, tags: e.target.value.split(' ')})} placeholder="Tags" />
                </div>
                <div className="">
                    <input type="file" accept=".jpg, .jpeg, .png"  name="selectedFile"  onChange={(e) => setPostData({ ...postData, selectedFile: e.target.files[0] })} />                    
                </div>
                    <div className="flex w-full mt-2">
                    <button type="submit" className="w-1/2 p-3 bg-red-400 text-white rounded-l">Submit</button>
                    <button type="reset" className="w-1/2 p-3 bg-gray-200 rounded-r" onClick={clear}>Clear</button>
                </div>
            </form>
        </div>
    )
}

export default Form;
