import React from 'react';
import { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { fetchPosts } from './actions/actions';
import Header from './components/header';
import Footer from './components/footer';
import { BrowserRouter as Router,  Route, Routes } from 'react-router-dom';
import Form from './components/Forms/Form';
import Auth from './components/Auth/Auth';
import { PaginatedItems } from './components/pagination';
import PostDetails from './components/Posts/Post/PostDetails';

const App = () => {

  const[currentId, setCurrentId] = useState();
  const dispatch = useDispatch();

  useEffect(() => {
     dispatch(fetchPosts());
  },[currentId, dispatch])

  return (
    <div className="w-full md:mx-auto md:w-2/3">
      <Router> 
        <Header/>
        <Routes>
          <Route path='/' exact element={<PaginatedItems itemsPerPage={6} setCurrentId={currentId} />} />
          <Route path='/post/details' exact element={<PostDetails/>}/>
          <Route path='/post' exact element={<Form currentId={currentId} setCurrentId={setCurrentId}/>}/>
          <Route path='/auth' exact element={<Auth/>}/>
        </Routes>
        <Footer/>
      </Router>  
    </div>
  );
}

export default App;
