import { configureStore } from "@reduxjs/toolkit";
import  AuthReducer  from "../reducers/AuthSlice";
import PostReducer from "../reducers/PostSlice";

const store = configureStore({
    reducer: {
        posts: PostReducer,
        auth: authReducer
    }
});

export default store;
