import axios from 'axios';

const url = 'http://localhost:5000/';

export const getPosts = () => axios.get(`${url}posts/getPosts`);

export const createPost = (newPost) => axios({
        method: 'post',
        url: `${url}posts/createPost`,
        data: newPost,
        headers: {
            'content-type': 'multipart/form-data'
        }        
    });


export const updatePost = (postId, post) => axios({
        method:'patch',
        url:`${url}posts/updatePost/${postId}`,
        data: post,
        headers: {
            'content-type': 'multipart/form-data'
        }        
});

export const deletePost = (postId) => axios.delete(`${url}/posts/deletePost/${postId}`);

export const likePost = (postId) => axios.patch(`${url}posts//likePost/${postId}`);

export const auth = (postData) => axios.post(url); 

export const signIn = (formData) => axios.post(`${url}/user/signin`, formData);
export const signUp = (formData) => axios.post(`${url}/user/signup`, formData);
