import { Container, AppBar, Typography, Grow, Grid, ThemeProvider, createTheme } from '@mui/material';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';

import Form from './components/Forms/Form';
import Posts from './components/Posts/Posts';
import { getPosts } from './components/Posts/PostsSlice';

import appStyles from './styles';
import logo from './images/logo.png';

const appBarTheme = createTheme({
  borderRadius: 15,
  margin: '30px 0',
  display: 'flex',
  flexDirection: 'row',
  justifyContent: 'center',
  alignItems: 'center',
});

const App = () => {

  const dispatch = useDispatch();

  useEffect(() => {
     dispatch(getPosts());
  },[])

  return (
          <Container maxWidth="lg">
            <ThemeProvider theme={appBarTheme}>
              <AppBar position="static" color="inherit">
                  <Typography variant="h6" align="left" fontFamily="billabong">Instagram</Typography>
                  <img src={logo}  height={60} width={60} />
              </AppBar>
            </ThemeProvider>
            <Grow in>
                <Container>
                    <Grid container justifyItems="space-between" alignItems="stretch" spacing={3}>
                        <Grid item xs={12} sm={7}>
                            <Posts/>
                        </Grid>
                        <Grid item xs={12} sm={7}>
                            <Form/>
                        </Grid>
                    </Grid>
                </Container>
            </Grow>
          </Container>
  );
}

export default App;
